onChange.script:
if currentValue.value:
	self.custom.deferUpdates = False
else:
	self.custom.deferUpdates = self.params.deferUpdates

if currentValue.value == 0:
	self.custom.lastSelection = previousValue.value
else:
	self.custom.lastSelection = currentValue.value

config.script:
self.custom.hasFocus = False
if self.custom.firstKeyTyped:
	self.custom.lostFocusAfterFirstKeyTyped = True

if event.key == 'Shift':
	self.custom.shiftIsDown = True
elif event.key == 'Control':
	self.custom.ctrlIsDown = True
elif event.key == 'Alt':
	self.custom.altIsDown = True
elif event.key == 'Tab':
	self.custom.firstEnterTyped = False

system.perspective.sendMessage('markdown-text-area-editing')

if event.key == '@':
	self.custom.shiftOrCtrlIsDown = False
	self.props.deferUpdates = False

if self.view.custom.mentioning != '':
	if event.key == 'Escape':
		self.view.custom.mentioning = ''
	elif event.key == 'ArrowDown':
		system.perspective.sendMessage('mention-down', {
			'domID': self.view.custom.domID
		})
	elif event.key == 'ArrowUp':
		system.perspective.sendMessage('mention-up', {
			'domID': self.view.custom.domID
		})
		self.view.custom.setCursorAt = self.view.custom.lastSelection

import re
import time

def refocus():
	self.custom.cursorWasAt = self.view.custom.selectionEnd
	time.sleep(0.2)
	self.focus()
	self.view.custom.setCursorAt = self.custom.cursorWasAt
	self.restoreUpdatesSettings()

def undoText():
	self.props.text = self.view.custom.undoText
	refocus()

def redoText():
	self.props.text = self.view.custom.redoText
	refocus()

if event.key == 'Shift':
	self.custom.shiftIsDown = False
elif event.key == 'Control':
	self.custom.ctrlIsDown = False
elif event.key == 'Alt':
	self.custom.altIsDown = False
elif event.key == 'Enter' and self.custom.lostFocusAfterFirstKeyTyped:
	self.custom.firstEnterTyped = True
else:
	self.custom.firstEnterTyped = False

self.custom.firstKeyTyped = True
textUpToCursor = self.props.text[:self.view.custom.selectionEnd]

if event.key != '@' and event.key != 'Shift':
	if event.key == 'Enter' and self.view.custom.mentioning != '':
		system.perspective.sendMessage('select-mention', {
			'domID': self.view.custom.domID
		})
	elif self.view.params.userMentions != 'None' and re.match('[\s\S]*\@\w{1,7}$', textUpToCursor):
		self.view.custom.mentioning = re.sub('[\s\S]*\@(\w{1,7})$', '\\1', textUpToCursor)
	else:
		self.view.custom.mentioning = ''
		if not self.view.custom.previewShowing:
			self.view.custom.deferUpdates = self.view.params.deferUpdates

if event.key == 'z' and self.custom.ctrlIsDown and self.view.custom.undoText != '':
	self.props.rejectUpdatesWhileFocused = False
	self.view.custom.redoText = self.props.text
	time.sleep(0.2)
	undoText()
elif event.key == 'y' and self.custom.ctrlIsDown and self.view.custom.redoText != '':
	self.props.rejectUpdatesWhileFocused = False
	self.view.custom.undoText = self.props.text
	time.sleep(0.2)
	redoText()
elif event.key != 'Control':
	self.view.custom.undoText = self.view.custom.redoText = ''

if self.custom.ctrlIsDown and self.custom.altIsDown and event.key == 'd':
	self.view.custom.debugging = not self.view.custom.debugging

messageHandlers.script:
if payload['domID'] == self.view.custom.domID:
	self.props.rejectUpdatesWhileFocused = False
	index = self.view.custom.selectionStart
	text = self.props.text
	if self.view.params.enableHTML:
		if self.view.params.openLinksInNewTab:
			link = '<a href="' + payload['url'] + '" target="_blank">' + payload['text'] + '</a>'
		else:
			link = '<a href="' + payload['url'] + '">' + payload['text'] + '</a>'
	else:
		link = '[' + payload['text'] + '](' + payload['url'] + ')'
	self.props.text = text[0:index] + link + text[index:]
	self.view.custom.setCursorAt = index + len(link)
	self.restoreUpdatesSettings()

if payload['domID'] == self.view.custom.domID:
	self.props.rejectUpdatesWhileFocused = False
	index = self.view.custom.selectionStart
	text = self.props.text
	try:
		att = ''
		if payload['width'] != '':
			att += ' width=' + str(payload['width'])
		if payload['height'] != '':
			att += ' height=' + str(payload['height'])
		att += ' align=' + payload['align']
		if payload['caption'] != '':
			pos = ' ' + payload['captionPosition']
			if pos == ' ':
				pos = ''
			att += ' caption' + pos + '=' + payload['caption'].replace('>', '&gt;')
		insert = '<[' + payload['type'] + '?id=' + str(payload['id']) + att + ']>'
		self.props.text = text[0:index] + insert + text[index:]
		self.view.custom.setCursorAt = index + len(insert)
		self.restoreUpdatesSettings()
	except Exception as e:
		system.perspective.print(str(e))

if payload['domID'] == self.view.custom.domID:
	self.view.custom.mentionSelectedIndex += 1
	if self.view.custom.mentionSelectedIndex == len(self.props.instances):
		self.view.custom.mentionSelectedIndex = 0
	system.perspective.sendMessage('mention-index', {
		'index': self.view.custom.mentionSelectedIndex
	})
	#self.focus()

if payload['domID'] == self.view.custom.domID:
	self.view.custom.mentionSelectedIndex -= 1
	if self.view.custom.mentionSelectedIndex < 0:
		self.view.custom.mentionSelectedIndex = len(self.props.instances) - 1
	system.perspective.sendMessage('mention-index', {
		'index': self.view.custom.mentionSelectedIndex
	})
	#self.focus()

customMethods.script:
if self.view.custom.selectionEnd == self.view.custom.selectionStart:
	system.perspective.sendMessage('insert-format', {
		'pre': '\n> Quote',
		'post': '',
		'domID': self.view.custom.domID
	})
else:
	system.perspective.sendMessage('insert-format', {
		'pre': '\n> ',
		'post': '',
		'domID': self.view.custom.domID
	})

pfix = ''
if self.view.params.text != '' and not self.view.params.text.endswith('\n'):
	pfix = '\n\n'
system.perspective.sendMessage('insert-format', {
	'pre': pfix + '|Col 1|Col 2|\n|-----|-----|\n| 1,1 | 1,2 |\n| 2,1 | 2,2 |\n',
	'post': '',
	'domID': self.view.custom.domID
})

messageHandlers.script:
if payload['id'] == self.view.custom.domID:
	system.perspective.sendMessage('insert-format', {
		'pre': '<span style="' + ('background-' if payload['isBackground'] else '') + 'color: ' + payload['color'] + '">',
		'post': '</span>',
		'domID': self.view.custom.domID
	})
