def browseHistoricalTags(path):
	tags = []
	#results = system.tag.browse(path).getResults()
	results = system.tag.browseHistoricalTags(path).getResults()

	if results != None:
		for result in results:
			if result.hasChildren() == True:
				tag = {'label': result.path.lastPathComponent, 'expanded': False, 'data': {'folder': result.path}}
				tag['items'] = browseHistoricalTags(result.path)
				if len(tag['items']) > 0:
					tags.append(tag)
			else:
				tags.append({'label': result.path.lastPathComponent, 'data': {'tag': result.path}})
	return tags



def browseTags(path):
	tags = []
	results = system.tag.browseTags(path)

	for result in results:
		if result.isFolder() or result.isUDT():
			tag = {'label': result.name, 'expanded': False, 'data': {'folder': result.fullPath}}
			tag['items'] = browseTags(result.fullPath)
			if len(tag['items']) > 0:
				tags.append(tag)
		else:
			tags.append({'label': result.name, 'data': {'tag': result.fullPath}, 'items': [], 'expanded': False})
	return tags

