def selectTag(treePath, index, tags, itemPath):
	count = 0
	for tag in tags:
		if tag['label'] == treePath[index]:
			itemPath.append(str(count))
			if index < len(treePath) and 'items' in tag:
				selectTag(treePath, index + 1, tag['items'], itemPath)
			break
		count += 1
	return itemPath

