if event.column == 'delete':
	axes = [axis for axis in self.view.custom.axes]
	axes.remove(axes[event.row])
	self.view.custom.axes = axes
elif event.column == 'singleAxis':
	# only allowed to select new single axis
	if event.value:
		# set the new value first, so that the time series chart will not momentarily flash all axes
		self.view.custom.axes[event.row][event.column] = event.value
		for i in range(len(self.view.custom.axes)):
			if i != event.row:
				self.view.custom.axes[i][event.column] = False

